mapper.update:
update notice
set title=#{title},
content=#{content},
nickname=#{nickname}, wtime=#{wtime}, hits=#{hits}
where idx=#{idx}

update notice
set hits=hits+1
where idx=#{idx}

update board
set title=#{title},
content=#{content},
nickname=#{nickname}, wtime=#{wtime}, hits=#{hits}
where idx=#{idx}
